import React, { createContext, useState, useEffect, useMemo, useReducer } from "react";
import axios from "axios";

export const themes = {
  light: {
    font: "black",
    background: "white",
  },
  dark: {
    font: "white",
    background: "black",
  },
};

// Beth
export const initialState = { 
  theme: themes.light, 
  data: [],
  favorites: [],//adicional
};

const ADD_TO_FAVORITES = 'ADD_TO_FAVORITES';
const REMOVE_FROM_FAVORITES = 'REMOVE_FROM_FAVORITES';

const favoritesReducer = (state = [], action) => {
  switch (action.type) {
    case ADD_TO_FAVORITES:
      return [...state, action.payload];
    case REMOVE_FROM_FAVORITES:
      return state.filter(card => card.id !== action.payload.id);
    default:
      return state;
  }
};  

export const ContextGlobal = createContext();

export const ContextProvider = ({ children }) => {
  const [theme, setTheme] = useState(themes.light);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true); 
  const [favorites, dispatchFavorites] = useReducer(favoritesReducer, [])

  useEffect(() => {
    getData();
  }, []);

    const getData = async () => {
      try {
        const urlApi = "https://jsonplaceholder.typicode.com/users";
        setLoading(true); 

        const res = await axios.get(urlApi);
        setData(res.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        setData([]); 
      } finally {
        setLoading(false); 
      }
    };

  const handleChangeTheme = () => {
    if (theme === themes.dark) setTheme(themes.light)
    if (theme === themes.light) setTheme(themes.dark)
  }

  const addToFavorites = card => {
    console.log('Adding to favorites:', card);
    dispatchFavorites({ type: ADD_TO_FAVORITES, payload: card });
  };

  const removeFromFavorites = card => {
    console.log('Removing from favorites:', card); 
    dispatchFavorites({ type: REMOVE_FROM_FAVORITES, payload: card });
  };

  const contextValue = useMemo(() => ({ theme, handleChangeTheme, data, favorites, addToFavorites, removeFromFavorites }), [theme, handleChangeTheme, data, favorites]);


    return (
    <ContextGlobal.Provider 
      value={contextValue}>
      {children}
    </ContextGlobal.Provider>
  );
};
