import React, { useEffect } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './Routes/Home';
import Detail from './Routes/Detail';
import Contact from './Routes/Contact';
import Favs from './Routes/Favs';
import './index.css';
import App from './App';
import { ContextProvider } from './Components/utils/global.context';
import Layout from './Components/Layout';

const storedTheme = localStorage.getItem('theme');
const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];

const initialAppState = {
  theme: storedTheme ? (storedTheme === 'dark' ? themes.dark : themes.light) : themes.light,
  data: [],
  favorites: storedFavorites,
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <ContextProvider initialState={initialAppState}>
      <Layout>
        <BrowserRouter>
          <Routes>
            <Route
              path="/"
              element={
                <AppWrapper />
              }
            >
              <Route path="/" element={<Home />} />
              <Route path="home" element={<Home />} />
              <Route path="dentist/:id" element={<Detail />} />
              <Route path="contacto" element={<Contact />} />
              <Route path="favs" element={<Favs />} />
            </Route>
          </Routes>
        </BrowserRouter>
      </Layout>
    </ContextProvider>
  </React.StrictMode>,
);

// Agrega un efecto para persistir el tema y los favoritos en el almacenamiento local
function persistAppState(theme, favorites) {
  localStorage.setItem('theme', theme === themes.light ? 'light' : 'dark');
  localStorage.setItem('favorites', JSON.stringify(favorites));
}

function AppWrapper() {
  const { state } = useGlobalContext();

  // Persistir el estado en el almacenamiento local cuando cambie
  useEffect(() => {
    persistAppState(state.theme, state.favorites);
  }, [state.theme, state.favorites]);

  return <App />;
}
