import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";

const Card = ({ name, username, id }) => {
  const { favorites, addToFavorites, removeFromFavorites, theme } = useContext(
    ContextGlobal
  );

  const isFavorite = (favorites || []).some((fav) => fav.id === id);

  const handleToggleFavorite = () => {
    if (isFavorite) {
      removeFromFavorites({ id, name, username });
    } else {
      addToFavorites({ id, name, username });
    }
  };

  const themeClass = theme === "dark" ? "dark" : "light";
  const textColor = theme === "dark" ? "white" : "black";

  return (
    <div className={`card ${themeClass}`}>
      <h3 style={{ color: textColor }}>{id}</h3>
      <p style={{ color: textColor }}>{name}</p>
      <p style={{ color: textColor }}>{username}</p>

      {/* Utilizar Link para navegar a la página de detalle */}
      <Link to={`/dentist/${id}`} className={`detail-link ${themeClass}`}>
        Ver detalle
      </Link>

      {/* Botón para agregar/quitar de favoritos */}
      <button
        onClick={handleToggleFavorite}
        className={`favButton ${isFavorite ? "favorited" : ""} ${themeClass}`}
      >
        {isFavorite ? "Remove from Favorites" : "Add to Favorites"}
      </button>
    </div>
  );
};

export default Card;
